      printf (",\n  0x%08x, 0x%08x, 0x%08x, 0x%08x, 0x%08x",
	      crc_remainder (i * 5 + 1), crc_remainder (i * 5 + 2),
	      crc_remainder (i * 5 + 3), crc_remainder (i * 5 + 4),
	      crc_remainder (i * 5 + 5));
